template.value:
@Override
protected void attachBaseContext(Context base) {
    initTheme(base);
    super.attachBaseContext(base);  
}

public void initTheme(Context context) {
    if (!AppPreferences.isDayMode(context)) {
        AppCompatDelegate.setDefaultNightMode(AppCompatDelegate.MODE_NIGHT_YES);
    } else {
        AppCompatDelegate.setDefaultNightMode(AppCompatDelegate.MODE_NIGHT_NO);
    }
}

public void setDayNightMode(Context context, boolean isDayMode) {
    if (context instanceof AppCompatActivity) {
        ((AppCompatActivity) context).getDelegate().setLocalNightMode( isDayMode
                ? AppCompatDelegate.MODE_NIGHT_NO : AppCompatDelegate.MODE_NIGHT_YES );
    } 
    if (isDayMode) {
        AppCompatDelegate.setDefaultNightMode(AppCompatDelegate.MODE_NIGHT_NO);
    } else {
        AppCompatDelegate.setDefaultNightMode(AppCompatDelegate.MODE_NIGHT_YES);
    }
}

MenuItem mDayNight = navigationView.getMenu().findItem(R.id.nav_day_night);
Switch dayNight = (Switch) mDayNight.getActionView();

dayNight.setChecked(!SharedPrefUtil.isDayMode(this));
dayNight.setOnCheckedChangeListener(new CompoundButton.OnCheckedChangeListener() {
    @Override
    public void onCheckedChanged(CompoundButton buttonView, boolean isChecked) {
        SharedPrefUtil.setDayMode(MainActivity.this, !isChecked);
    }
});
